import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Fetch data from the Flask backend
    fetch('http://127.0.0.1:5000/')
      .then((response) => response.text())  // Expecting text response from Flask
      .then((data) => setMessage(data))  // Set the response data to the state
      .catch((error) => console.log('Error fetching data:', error));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Tea Talks</h1>
        <p>{message || 'Loading message from Flask...'}</p> {/* Show Flask message here */}
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
